// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

contract Permissions {
   address owner;
   constructor() {
      owner = msg.sender;
      isAuthorised[owner] = true;

   }
   mapping (address=>bool) isAuthorised;
   
   modifier onlyOwner {
      require(msg.sender == owner, "Not owner of contract!");
      _;
   }

   modifier onlyAuthorisedPersonnel {
      require(isAuthorised[msg.sender], "User is not authorised!");
      _;
   }

   function authoriseUser(address _user) external onlyOwner {
      isAuthorised[_user] = true;
   }

   modifier costs(uint price) {
      if (msg.value >= price) {
         _;
      }
   }
}
